CREATE TABLE NOTE (NOTE_ID INTEGER NOT NULL, USER_ID INTEGER NOT NULL, NOTE_TITLE LONG VARCHAR NOT NULL, NOTE_CONTENT LONG VARCHAR NOT NULL, NOTE_DATE VARCHAR(30) NOT NULL, NOTE_TYPE VARCHAR(20) NOT NULL, THEME_ID VARCHAR(30) NOT NULL, LOCAL_ID INTEGER DEFAULT AUTOINCREMENT: start 1 increment 1  NOT NULL GENERATED ALWAYS AS IDENTITY, PRIMARY KEY (LOCAL_ID));
CREATE TABLE THEME (THEME_ID INTEGER DEFAULT AUTOINCREMENT: start 1 increment 1  NOT NULL GENERATED ALWAYS AS IDENTITY, THEME_NAME VARCHAR(20) NOT NULL, PRIM_COL VARCHAR(10) NOT NULL, SECO_COL VARCHAR(10) NOT NULL, TEXT_COL VARCHAR(10) NOT NULL, HINT_COL VARCHAR(10) NOT NULL, ACCE_COL VARCHAR(10) NOT NULL, BUT_COL VARCHAR(20) NOT NULL, PRIMARY KEY (THEME_ID))